import React, { useState } from 'react';
import {
  Container, TextField, Button, Typography, Paper, Alert
} from '@mui/material';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const AuthPage = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleAuth = async () => {
    setLoading(true);
    setError('');
    try {
      const res = await axios.post('http://20.244.56.144/evaluation-service/auth', {
        email: "uddeshya22154051@akgec.ac.in",
        name: "uddeshya tyagi",
        rollNo: "2200271540117",
        accessCode: "uuMbyY",
        clientID: "3eb1cce6-e9ca-42d2-86dd-b1f74802187a",
        clientSecret: "VfqmqdgnTPVFGufx"
      });
      localStorage.setItem("authToken", res.data.token);
      navigate("/shortener");
    } catch (err) {
      setError("Authentication failed. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container maxWidth="sm" sx={{ mt: 6 }}>
      <Paper elevation={3} sx={{ p: 4 }}>
        <Typography variant="h5" gutterBottom>Authenticate to Continue</Typography>
        {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
        <Button variant="contained" fullWidth onClick={handleAuth} disabled={loading}>
          {loading ? 'Authenticating...' : 'Authenticate'}
        </Button>
      </Paper>
    </Container>
  );
};

export default AuthPage;
