import React, { useState } from 'react';
import { generateShortcode, validateUrl } from '../utils/validators';

const UrlShortenerPage = () => {
  const [inputs, setInputs] = useState([
    { longUrl: '', validity: '', shortcode: '' },
  ]);
  const [results, setResults] = useState([]);

  const handleChange = (index, field, value) => {
    const newInputs = [...inputs];
    newInputs[index][field] = value;
    setInputs(newInputs);
  };

  const addUrlInput = () => {
    if (inputs.length < 5) {
      setInputs([...inputs, { longUrl: '', validity: '', shortcode: '' }]);
    }
  };

  const handleSubmit = () => {
    const stored = JSON.parse(localStorage.getItem("urlData")) || {};

    const newResults = inputs.map(input => {
      if (!validateUrl(input.longUrl)) return null;

      const shortcode = input.shortcode?.trim() || generateShortcode();
      if (stored[shortcode]) return null; // shortcode collision

      const createdAt = Date.now();
      const validityMinutes = parseInt(input.validity) || 30;
      const expiresAt = createdAt + validityMinutes * 60000;

      const entry = {
        longUrl: input.longUrl,
        createdAt,
        expiresAt,
        shortcode,
        clicks: [],
      };

      stored[shortcode] = entry;
      return entry;
    }).filter(Boolean);

    localStorage.setItem("urlData", JSON.stringify(stored));
    setResults(newResults);
  };

  return (
    <div className="max-w-4xl mx-auto p-4">
      <h1 className="text-3xl font-bold mb-4">URL Shortener (Client-side)</h1>
      <button onClick={addUrlInput} className="bg-blue-500 text-white px-4 py-2 rounded mb-4">Add Another URL</button>
      {inputs.map((input, i) => (
        <div key={i} className="bg-gray-100 p-4 rounded mb-4">
          <input
            type="text"
            placeholder="Long URL"
            value={input.longUrl}
            onChange={e => handleChange(i, 'longUrl', e.target.value)}
            className="w-full p-2 mb-2 border rounded"
          />
          <input
            type="number"
            placeholder="Validity (minutes)"
            value={input.validity}
            onChange={e => handleChange(i, 'validity', e.target.value)}
            className="w-full p-2 mb-2 border rounded"
          />
          <input
            type="text"
            placeholder="Preferred Shortcode"
            value={input.shortcode}
            onChange={e => handleChange(i, 'shortcode', e.target.value)}
            className="w-full p-2 border rounded"
          />
        </div>
      ))}
      <button onClick={handleSubmit} className="bg-green-500 text-white px-4 py-2 rounded">Submit</button>

      {results.length > 0 && (
        <div className="mt-6">
          <h2 className="text-xl font-semibold mb-2">Shortened URLs:</h2>
          {results.map((r, i) => (
            <div key={i} className="p-4 bg-white shadow rounded mb-2">
              <p>Short URL: <a href={`/${r.shortcode}`} className="text-blue-600 underline">{window.location.origin}/{r.shortcode}</a></p>
              <p>Expires At: {new Date(r.expiresAt).toLocaleString()}</p>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default UrlShortenerPage;
